CREATE OR REPLACE TRIGGER SAL_INVOICES_INTEGRATION_TRI
BEFORE DELETE OR INSERT OR UPDATE
ON SAL_INVOICES_INTEGRATION
REFERENCING NEW AS NEW OLD AS OLD
FOR EACH ROW
DECLARE
CURSOR C1(V_DOCUMENT_ID NUMBER, V_DEPARTMENT_ID NUMBER) IS
    SELECT COUNT(*)
    FROM SAL_INVOICES
    WHERE DOCUMENT_ID = V_DOCUMENT_ID
    AND DEPARTMENT_ID = V_DEPARTMENT_ID;

CURSOR C2(V_DOCUMENT_ID NUMBER, V_DEPARTMENT_ID NUMBER) IS
    SELECT DOCUMENT_ID, DEPARTMENT_ID
    FROM SAL_INVOICES
    WHERE DOCUMENT_ID = V_DOCUMENT_ID
    AND DEPARTMENT_ID = V_DEPARTMENT_ID;
V_C2 C2%ROWTYPE;

CURSOR C3(V_DEPARTMENT_ID NUMBER) IS
	SELECT SAL_LEDGER_TRANSACTIONS_ID
	FROM DEPARTMENTS
	WHERE DEPARTMENT_ID = V_DEPARTMENT_ID;
V_SAL_LEDGER_TRANSACTIONS_ID NUMBER;

CURSOR C4(V_DEPARTMENT_ID NUMBER) IS
	SELECT STORES_ID
	FROM DEPARTMENTS_STORES
	WHERE DEPARTMENT_ID = V_DEPARTMENT_ID;
V_STORES_ID NUMBER;

CURSOR C5(V_DEPARTMENT_ID NUMBER) IS
	SELECT TREASURIES_ID
	FROM DEPARTMENTS_TREASURIES
	WHERE DEPARTMENT_ID = V_DEPARTMENT_ID;
V_TREASURIES_ID NUMBER;

CURSOR C6 IS
    SELECT CURRENCIES_ID
    FROM CURRENCIES
    WHERE DEFAULT_CURRENCY = 1;
V_CURRENCIES_ID NUMBER;

CURSOR C7(V_CUSTOMER_ID NUMBER) IS
	SELECT
	CUSTOMER_ID                            ,
	CREDIT_LIMIT_DAYS
	FROM CUSTOMERS
	WHERE CUSTOMER_ID = V_CUSTOMER_ID;
V_C7 C7%ROWTYPE;


V_COUNT NUMBER :=0;
V_FINANCIAL_YEARS_ID NUMBER;
V_FINANCIAL_PERIODS_ID NUMBER;
V_DOCUMENT_NO VARCHAR2(50);
V_COMPUTER_SERIAL VARCHAR2(50);
V_TRANSACTIONS_ID NUMBER;
V_CONFIRMED NUMBER;
V_ERROR_MSG VARCHAR2(500);

CURSOR C8(V_DOCUMENT_ID NUMBER, V_DEPARTMENT_ID NUMBER) IS
    SELECT COUNT(*)
    FROM SAL_INVOICES
    WHERE DOCUMENT_ID = V_DOCUMENT_ID
    AND DEPARTMENT_ID = V_DEPARTMENT_ID
    AND CONFIRMED = 1;

BEGIN
    V_C2.DOCUMENT_ID := NULL;
    V_C2.DEPARTMENT_ID := NULL;

    IF INSERTING THEN

        GET_FINANCIAL_YEARS_PERIODS_BR(:NEW.BRANCH_ID, :NEW.DOCUMENT_DATE, V_FINANCIAL_YEARS_ID ,V_FINANCIAL_PERIODS_ID);

        IF V_FINANCIAL_YEARS_ID IS NULL THEN
            --RAISE_APPLICATION_ERROR(-20090, 'Financial year not defined');
	    V_ERROR_MSG := 'Financial year not defined';
        END IF;

        OPEN C3 (:NEW.DEPARTMENT_ID);
        FETCH C3 INTO V_SAL_LEDGER_TRANSACTIONS_ID;
        CLOSE C3;

        IF V_SAL_LEDGER_TRANSACTIONS_ID IS NULL THEN
            --RAISE_APPLICATION_ERROR(-20091, 'Default showroom sales link not defined');
	    V_ERROR_MSG := 'Default showroom sales link not defined';
        END IF;

        OPEN C4 (:NEW.DEPARTMENT_ID);
        FETCH C4 INTO V_STORES_ID;
        CLOSE C4;

        IF V_STORES_ID IS NULL THEN
            --RAISE_APPLICATION_ERROR(-20092, 'Default showroom store not defined');
	    V_ERROR_MSG := 'Default showroom store not defined';
        END IF;

        OPEN C5 (:NEW.DEPARTMENT_ID);
        FETCH C5 INTO V_TREASURIES_ID;
        CLOSE C5;

        IF V_TREASURIES_ID IS NULL THEN
            --RAISE_APPLICATION_ERROR(-20093, 'Default showroom treasury not defined');
	    V_ERROR_MSG := 'Default showroom treasury not defined';
        END IF;

        OPEN C6;
        FETCH C6 INTO V_CURRENCIES_ID;
        CLOSE C6;


       	OPEN C7(:NEW.CUSTOMER_ID);
       	FETCH C7 INTO V_C7;
        CLOSE C7;

	V_C7.CREDIT_LIMIT_DAYS := NVL(V_C7.CREDIT_LIMIT_DAYS,30);

        V_CURRENCIES_ID := NVL(V_CURRENCIES_ID,1);


        V_COUNT :=0;
        OPEN C1(:NEW.DOCUMENT_ID, :NEW.DEPARTMENT_ID);
        FETCH C1 INTO V_COUNT;
        CLOSE C1;



        IF V_COUNT = 0 THEN

            INSERT INTO SAL_INVOICES
                  (
              		DOCUMENT_ID                  ,
              		DEPARTMENT_ID                ,
              		DOCUMENT_TYPES_ID            ,
              		DOCUMENT_NO                  ,
              		DOCUMENT_DATE                ,
			DUE_DATE,
              		CUSTOMER_ID                  ,
              		CASH_CUSTOMER_NAME           ,
              		SALES_MAN_ID                 ,
              		PAYMENT_TYPE                 ,
              		STORES_ID                    ,
              		TREASURIES_ID                ,
              		CURRENCIES_ID                ,
              		EXCHANGE_RATE                ,
              		AMOUNT_PAID                  ,
              		CONFIRMED                    ,
              		CANCELED                     ,
              		JOURNALS_ID                  ,
              		BRANCH_ID                    ,
              		SAL_LEDGER_TRANSACTIONS_ID   ,
              		INVOICE_RETURNED_COMPLETLY   ,
              		TOTAL_PRICE                  ,
              		INVOICE_DISCOUNT             ,
              		ITEM_DISCOUNT                ,
              		EXPENSES                     ,
                    SERVICES                     ,
                    NET_PRICE                    ,
                    INVOICE_DISCOUNT_RETURNED    ,
                    NOTES                        ,
                    CREATED_BY                   ,
                    CREATION_DATE                ,
                    CREATION_MACHINE             ,
                    UPDATED_BY                   ,
                    UPDATED_DATE                 ,
                    UPDATED_MACHINE              ,
                    CUSTOMER_TEL                 ,
                    CUSTOMER_MOBILE              ,
                    COST_CENTER_ID               ,
                    FINANCIAL_YEARS_ID           ,
                    DELIVERY_FLAG                ,
                    DOCUMENT_NO_JOURNAL          ,
                    CASH_AMOUNT                  ,
                    SALES_TAX                    ,
                    ADD_SUBTRACT_TAX             ,
                    TAX_INVOICE                  ,
                    POS_FLAG                     ,
                    ADVANCED_PAYMENT             ,
                    TOTAL_COST                   ,
                    WORKFLOW_SUBMITTED
                )
            VALUES
                  (
              		:NEW.DOCUMENT_ID                  ,
              		:NEW.DEPARTMENT_ID                ,
              		:NEW.DOCUMENT_TYPES_ID            ,
              		:NEW.DOCUMENT_NO                  ,
              		:NEW.DOCUMENT_DATE                ,
			:NEW.DOCUMENT_DATE + NVL(V_C7.CREDIT_LIMIT_DAYS, 30),
              		:NEW.CUSTOMER_ID                  ,
              		:NEW.CASH_CUSTOMER_NAME           ,
              		:NEW.SALES_MAN_ID                 ,
              		:NEW.PAYMENT_TYPE                 ,
              		V_STORES_ID                    ,
              		V_TREASURIES_ID                ,
              		V_CURRENCIES_ID                ,
              		1                ,
              		0                  ,
              		2                    ,
              		2                     ,
              		NULL                  ,
              		:NEW.BRANCH_ID                    ,
              		V_SAL_LEDGER_TRANSACTIONS_ID   ,
              		2   ,
              		0                  ,
              		:NEW.INVOICE_DISCOUNT             ,
              		0                ,
              		0                     ,
                    0                   ,
                    0                    ,
                    0    ,
                    :NEW.NOTES                        ,
                    :NEW.CREATED_BY                   ,
                    SYSDATE                ,
                    NULL             ,
                    NULL                   ,
                    NULL                 ,
                    NULL              ,
                    :NEW.CUSTOMER_TEL                 ,
                    :NEW.CUSTOMER_MOBILE              ,
                    NULL               ,
                    V_FINANCIAL_YEARS_ID           ,
                    2                ,
                    NULL          ,
                    0                 ,
                    0                    ,
                    0             ,
                    0                  ,
                    2                     ,
                    0             ,
                    0                   ,
                    2
                );

        END IF;

        INSERT INTO SAL_INVOICES_ITEMS
            (
                DOCUMENT_ID             ,
                DEPARTMENT_ID           ,
                ARRANGMENT_NO           ,
                ITEMS_ID                ,
                UNITS_ID                ,
                UNIT_QUANTITY           ,
                UNIT_FACTOR             ,
                ITEM_PRICE              ,
                TOTAL_PRICE             ,
                ITEM_DISCOUNT           ,
                TOTAL_DISCOUNT          ,
                ITEM_COST               ,
                UNIT_QUANTITY_RETURNED  ,
                BONUS_ITEM_FLAG         ,
                EXPIRY_DATE             ,
                NOTES                   ,
                CREATED_BY              ,
                CREATION_DATE           ,
                CREATION_MACHINE        ,
                UPDATED_BY              ,
                UPDATED_DATE            ,
                UPDATED_MACHINE         ,
                SALES_TAX_PER
            )
        VALUES
            (
                :NEW.DOCUMENT_ID             ,
                :NEW.DEPARTMENT_ID           ,
                :NEW.ARRANGMENT_NO           ,
                :NEW.ITEMS_ID                ,
                :NEW.UNITS_ID                ,
                :NEW.UNIT_QUANTITY           ,
                :NEW.UNIT_FACTOR             ,
                :NEW.ITEM_PRICE              ,
                :NEW.ITEM_PRICE * :NEW.UNIT_QUANTITY,
                0           ,
                0          ,
                0               ,
                0  ,
                2         ,
                NULL             ,
                NULL                   ,
                :NEW.CREATED_BY              ,
                SYSDATE           ,
                NULL        ,
                NULL              ,
                NULL            ,
                NULL         ,
                0
            );

    ELSIF UPDATING THEN

        GET_FINANCIAL_YEARS_PERIODS_BR(:NEW.BRANCH_ID, :NEW.DOCUMENT_DATE, V_FINANCIAL_YEARS_ID ,V_FINANCIAL_PERIODS_ID);

        IF V_SAL_LEDGER_TRANSACTIONS_ID IS NULL THEN
            --RAISE_APPLICATION_ERROR(-20090, 'Financial year not defined');
		V_ERROR_MSG := 'Financial year not defined';
        END IF;

        OPEN C3 (:NEW.DEPARTMENT_ID);
        FETCH C3 INTO V_SAL_LEDGER_TRANSACTIONS_ID;
        CLOSE C3;

        IF V_SAL_LEDGER_TRANSACTIONS_ID IS NULL THEN
            --RAISE_APPLICATION_ERROR(-20091, 'Default showroom sales link not defined');
		V_ERROR_MSG := 'Default showroom sales link not defined';
        END IF;

        OPEN C4 (:NEW.DEPARTMENT_ID);
        FETCH C4 INTO V_STORES_ID;
        CLOSE C4;

        IF V_STORES_ID IS NULL THEN
            --RAISE_APPLICATION_ERROR(-20092, 'Default showroom store not defined');
		V_ERROR_MSG := 'Default showroom store not defined';
        END IF;

        OPEN C5 (:NEW.DEPARTMENT_ID);
        FETCH C5 INTO V_TREASURIES_ID;
        CLOSE C5;

        IF V_TREASURIES_ID IS NULL THEN
            --RAISE_APPLICATION_ERROR(-20093, 'Default showroom treasury not defined');
       	    V_ERROR_MSG := 'Default showroom treasury not defined';
        END IF;

        OPEN C6;
        FETCH C6 INTO V_CURRENCIES_ID;
        CLOSE C6;

        V_CURRENCIES_ID := NVL(V_CURRENCIES_ID,1);

	V_COUNT := 0;	
        OPEN C8 (:NEW.DOCUMENT_ID, :NEW.DEPARTMENT_ID);
        FETCH C8 INTO V_COUNT;
        CLOSE C8;

	IF V_COUNT > 0 THEN
       	    V_ERROR_MSG := 'INVOICE IS POSTED';
        ELSE

        IF  :NEW.DOCUMENT_NO <> :OLD.DOCUMENT_NO OR
            :NEW.DOCUMENT_DATE <> :OLD.DOCUMENT_DATE OR
            :NEW.CUSTOMER_ID <> :OLD.CUSTOMER_ID OR
            :NEW.CASH_CUSTOMER_NAME <> :OLD.CASH_CUSTOMER_NAME OR
            :NEW.SALES_MAN_ID <> :OLD.SALES_MAN_ID OR
            :NEW.PAYMENT_TYPE <> :OLD.PAYMENT_TYPE OR
            :NEW.INVOICE_DISCOUNT <> :OLD.INVOICE_DISCOUNT OR
            :NEW.NOTES <> :OLD.NOTES OR
            :NEW.CUSTOMER_TEL <> :OLD.CUSTOMER_TEL OR
            :NEW.CUSTOMER_MOBILE <> :OLD.CUSTOMER_MOBILE THEN



            UPDATE SAL_INVOICES
            SET
              		DOCUMENT_NO = :NEW.DOCUMENT_NO,
              		DOCUMENT_DATE  = :NEW.DOCUMENT_DATE               ,
              		CUSTOMER_ID = :NEW.CUSTOMER_ID                 ,
              		CASH_CUSTOMER_NAME = CASH_CUSTOMER_NAME           ,
              		SALES_MAN_ID  = :NEW.SALES_MAN_ID               ,
              		PAYMENT_TYPE = :NEW.PAYMENT_TYPE                ,
              		INVOICE_DISCOUNT = :NEW.INVOICE_DISCOUNT            ,
                    NOTES = :NEW.NOTES                       ,
                    UPDATED_DATE = SYSDATE                ,
                    CUSTOMER_TEL = :NEW.CUSTOMER_TEL                ,
                    CUSTOMER_MOBILE = :NEW.CUSTOMER_MOBILE             ,
                    FINANCIAL_YEARS_ID = V_FINANCIAL_PERIODS_ID
             WHERE DOCUMENT_ID = :OLD.DOCUMENT_ID
             AND DEPARTMENT_ID = :OLD.DEPARTMENT_ID;
        END IF;

        IF  :NEW.ITEMS_ID <> :OLD.ITEMS_ID OR
            :NEW.UNITS_ID <> :OLD.UNITS_ID OR
            :NEW.UNIT_QUANTITY <> :OLD.UNIT_QUANTITY OR
            :NEW.UNIT_FACTOR <> :OLD.UNIT_FACTOR OR
            :NEW.ITEM_PRICE <> :OLD.ITEM_PRICE OR
            :NEW.TOTAL_PRICE <> :OLD.TOTAL_PRICE THEN


            UPDATE SAL_INVOICES_ITEMS
            SET
                ITEMS_ID = :NEW.ITEMS_ID               ,
                UNITS_ID = :NEW.UNITS_ID               ,
                UNIT_QUANTITY = :NEW.UNIT_QUANTITY          ,
                UNIT_FACTOR = :NEW.UNIT_FACTOR            ,
                ITEM_PRICE = :NEW.ITEM_PRICE             ,
                TOTAL_PRICE = :NEW.TOTAL_PRICE            ,
                UPDATED_DATE = SYSDATE
             WHERE DOCUMENT_ID = :OLD.DOCUMENT_ID
             AND DEPARTMENT_ID = :OLD.DEPARTMENT_ID
             AND ARRANGMENT_NO = :OLD.ARRANGMENT_NO;

        END IF;
	END IF;

    ELSIF DELETING THEN

	V_COUNT := 0;	
        OPEN C8 (:OLD.DOCUMENT_ID, :OLD.DEPARTMENT_ID);
        FETCH C8 INTO V_COUNT;
        CLOSE C8;

	IF V_COUNT > 0 THEN
       	    V_ERROR_MSG := 'INVOICE IS POSTED';
        ELSE

	    IF :OLD.ARRANGMENT_NO <> 1 THEN

            DELETE FROM SAL_INVOICES_ITEMS
            WHERE DOCUMENT_ID = :OLD.DOCUMENT_ID
            AND DEPARTMENT_ID = :OLD.DEPARTMENT_ID
            AND ARRANGMENT_NO = :OLD.ARRANGMENT_NO;

	    ELSE


            DELETE FROM SAL_INVOICES_ITEMS
            WHERE DOCUMENT_ID = :OLD.DOCUMENT_ID
            AND DEPARTMENT_ID = :OLD.DEPARTMENT_ID
            AND ARRANGMENT_NO = :OLD.ARRANGMENT_NO;

            DELETE FROM SAL_INVOICES
            WHERE DOCUMENT_ID = :OLD.DOCUMENT_ID
            AND DEPARTMENT_ID = :OLD.DEPARTMENT_ID;

	    END IF;
	END IF;
    END IF;
EXCEPTION
	WHEN OTHERS THEN
            INSERT INTO SAL_INVOICES_INTEGRATION_ERR
                  (
              		DOCUMENT_ID                  ,
              		DEPARTMENT_ID                ,
              		DOCUMENT_TYPES_ID            ,
              		DOCUMENT_NO                  ,
              		DOCUMENT_DATE                ,
			ERROR_MESSAGE
		)
	VALUES
		(
              		:NEW.DOCUMENT_ID                  ,
              		:NEW.DEPARTMENT_ID                ,
              		:NEW.DOCUMENT_TYPES_ID            ,
              		:NEW.DOCUMENT_NO                  ,
              		:NEW.DOCUMENT_DATE                ,
			V_ERROR_MSG
		);


END;
/


